script:
frappe.ui.form.on("Payment Entry", {
    //refresh:set_fields,
    party_type:set_fields,
    party:function(frm){

    }
});

function set_fields(){
    console.log(frappe.route_options.supplier);
    cur_frm.set_value("party",frappe.route_options.supplier || frappe.route_options.customer);
    delete frappe.route_options.supplier;
    delete frappe.route_options.customer;
    frappe.call({              
        method:"supplify_erpnext.supplify_erpnext.doctype.drop_ship_invoice.drop_ship_invoice.get_account",
        args:{
            "company":cur_frm.doc.company,
    "party_type":cur_frm.doc.party_type
        },
        callback:function(r){
            console.log("Account", r.message);
            if(r.party_type = "Supplier"){
                    cur_frm.set_value("paid_to", r.message);
            }else{
                cur_frm.set_value("paid_from", r.message);
            }
        }
    });
}
